const miModulo=(()=>{"use strict";let e=document.querySelector("#name"),t=document.querySelector("#email"),a=document.querySelector("#asunto"),r=document.querySelector("#mensaje"),i=document.querySelector("#reset");document.querySelector("#enviar");let n=document.querySelector(".contacto__error1"),o=document.querySelector(".contacto__error2"),s=document.querySelector(".contacto__error3"),l=document.querySelector(".contacto__error4");function c(e){/^[a-zA-Z ]{2,50}$/.test(e.value)?(e.classList.remove("invalido"),n.innerText=""):(e.classList.add("invalido"),n.innerText="Nombre no valido")}function d(e){/^([a-zA-Z0-9_\-\.]+)@([a-zA-Z0-9_\-\.]+)\.([a-zA-Z]{2,5})$/.test(e.value)?(e.classList.remove("invalido"),o.innerText=""):(e.classList.add("invalido"),o.innerText="Email no valido")}function v(e){/^[a-zA-Z0-9 ]{2,300}$/.test(e.value)?(e.classList.remove("invalido"),s.innerText=""):(e.classList.add("invalido"),s.innerText="El asunto no puede estar vacio")}function u(e){/^[a-zA-Z0-9 ]{2,300}$/.test(e.value)?(e.classList.remove("invalido"),l.innerText="",console.log("Mensaje valido")):(e.classList.add("invalido"),l.innerText="Agrega un mensaje mayor de 2 hasta 300 palabras")}e.addEventListener("blur",()=>{c(e)}),t.addEventListener("blur",()=>{d(t)}),a.addEventListener("blur",()=>{v(a)}),r.addEventListener("blur",()=>{u(r)}),i.addEventListener("click",()=>{e.classList.remove("invalido"),t.classList.remove("invalido"),a.classList.remove("invalido"),r.classList.remove("invalido"),contador=0})})();